const ahus = [
  'AHU-01', 'AHU-02', 'AHU-03', 'AHU-04', 'AHU-05',
  'AHU-06', 'AHU-07', 'AHU-08', 'AHU-09', 'AHU-10', 'AHU-11'
];

const messages = [];

ahus.forEach(ahu => {
  const temperature = +(20 + Math.random() * 12).toFixed(1);
  const humidity = +(40 + Math.random() * 30).toFixed(1);
  const fan_status = Math.random() > 0.15 ? 'ON' : 'OFF';

  let status = 'OK';
  if (fan_status === 'OFF') {
    status = 'ALARM';
  } else if (temperature < 20 || temperature > 28 || humidity > 65) {
    status = 'WARNING';
  }

  const timestamp = new Date().toISOString();

  messages.push({
    topic: `hvac/planta1/${ahu}`,
    payload: {
      plantId: 'planta1',
      stationId: ahu,
      timestamp,
      points: {
        temperature: {
          value: temperature,
          unit: '°C',
          quality: status === 'OK' ? 'GOOD' : status === 'ALARM' ? 'BAD' : 'UNCERTAIN'
        },
        humidity: {
          value: humidity,
          unit: '%',
          quality: status === 'OK' ? 'GOOD' : status === 'ALARM' ? 'BAD' : 'UNCERTAIN'
        },
        fan_status: {
          value: fan_status,
          unit: '',
          quality: status === 'OK' ? 'GOOD' : status === 'ALARM' ? 'BAD' : 'UNCERTAIN'
        },
        status: {
          value: status,
          unit: '',
          quality: status === 'OK' ? 'GOOD' : status === 'ALARM' ? 'BAD' : 'UNCERTAIN'
        }
      }
    }
  });
});

return [messages];
